// Récupère les données du nœud Webhook (en remplaçant "Webhook" par le nom du nœud Webhook dans votre workflow)
const webhookData = $node["Webhook"].json;

// Fonction pour convertir la valeur de sameSite
function convertSameSite(value) {
    // Conversion spécifique des valeurs de sameSite
    const conversionMap = {
        "unspecified": "None",
        "lax": "Lax",
        "strict": "Strict"
    };

    // Si la valeur existe dans le tableau de conversion, on la convertit
    if (value in conversionMap) {
        return conversionMap[value];
    }

    // Si la valeur est déjà une des valeurs acceptées par Selenium
    const allowedValues = ["Strict", "Lax", "None"];
    if (allowedValues.includes(value)) {
        return value;
    } else {
        // Si la valeur n'est pas reconnue, on la remplace par "Lax" (par défaut)
        return "Lax";
    }
}

// Vérifiez et traitez les données des cookies
if (webhookData.body && webhookData.body.cookies) {
    let items = [];
    for (const cookieObject of webhookData.body.cookies) {
        if (cookieObject.cookie) {
            // Convertir la valeur de sameSite
            cookieObject.cookie.sameSite = convertSameSite(cookieObject.cookie.sameSite);

            // Ajouter le cookie à la liste des items
            items.push({
                json: cookieObject.cookie
            });
        }
    }
    return items;
}

// Si les cookies ne sont pas trouvés, renvoyer un tableau vide
return [];

